    <div id="btnContainer" className="container">
        <button className="btn active mb-3 me-2 mt-3" onClick={()=>changeLayout('list')}><img src={SVGLIST}/> List</button> 
        <button className="btn active mb-3 mt-3" onClick={()=>changeLayout('grid')}><img src={SVGGRID}/> Grid</button>
    </div>
    )
    }
export default Product;
