import { useEffect, useState } from "react";
import {useParams} from "react-router-dom"

function ProductDetail() {
    const [productDetail, setProductDetail] = useState({});

    const params = useParams();
  
    useEffect(() => {
      const id = params.id;
      fetchProductDetails(id);
    }, []);
  
    const fetchProductDetails = (id) => {
      const productDetailsUrl = `https://fakestoreapi.com/products/${id}`;
      fetch(productDetailsUrl)
        .then((response) => response.json())
        .then((result) => {
          console.log(result);
          setProductDetail(result);
        });
    };
    return (
        <div>
          <div className="container ">
            <div className="row justify-content-center">
              <div className="col col-lg-6 mt-5">
                <div className="container text-center">
                  <img src={productDetail.image} />
                  <h1>{productDetail.Title}</h1>
                </div>
                <table className="table table-hover">
                  <tbody>
                    <tr>
                      <th scope="row">Plot</th>
                      <td>{productDetail.Plot}</td>
                    </tr>
                    <tr>
                      <th scope="row">Director</th>
                      <td>{productDetail.Director}</td>
                    </tr>
                    <tr>
                      <th scope="row">Writer</th>
                      <td>{productDetail.Writer}</td>
                    </tr>
                    <tr>
                      <th scope="row">Year</th>
                      <td>{productDetail.Year}</td>
                    </tr>
                    <tr>
                      <th scope="row">Rating</th>
                      <td>{productDetail.imdbRating}</td>
                    </tr>
                    <tr>
                      <th scope="row">Votes</th>
                      <td>{productDetail.imdbVotes}</td>
                    </tr>
                    <tr>
                      <th scope="row">Actors</th>
                      <td>{productDetail.Actors}</td>
                    </tr>
                    <tr>
                      <th scope="row">Earning</th>
                      <td>{productDetail.BoxOffice}</td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      );
}

export default ProductDetail
