import "./Product.css"

import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function Product() { 
  const [state, setState] = useState([]);

  const fetchData = async () => {
    return await fetch("https://fakestoreapi.com/products/")
          .then((response) => response.json())
          .then((data) => setState(data));
  }

  useEffect(() => {
    fetchData();
  },[])

  const listView = () => {
    for (i = 0; i < elements.length; i++) {
        elements[i].style.width = "100%";
      }
    }

  const gridView =() =>{
    for (i = 0; i < elements.length; i++) {
        elements[i].style.width = "50%";
      }
    }

    return (
        <div className="album py-5 bg-light">
            <div className="container">
                <div id="btnContainer">
                    <button className="btn mb-3" onClick={listView}><i className="fa fa-bars"></i> List</button> 
                    <button className="btn active mb-3" onClick={gridView}><i className="fa fa-th-large "></i> Grid</button>
                </div>
                <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-3">{
                    state.map((item) => ( 
                    <div className="col" key = { item.id }>
                        <div className="card shadow-sm">
                            <img className="card-img-top imgheight" src={item.image} alt="Card image cap"/>
                            <div className="card-body">
                                <p className="card-text">{item.price} $</p>
                                <p className="card-text containerx">{item.title}</p>
                                <div className="d-flex justify-content-between align-items-center">
                                    <div className="btn-group">
                                        <Link type="button" to={`/productdetails/${item.id}`} className="btn btn-primary btn-lg px-4 gap-3">Show Details</Link>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                ))
                }
                </div>
            </div>
    </div>

    )
    }
export default Product;
